variables.script:
function	requiredLength(field,minLength,maxLength,type)	{

//   6-4-2013 DFG & 6-6-2013 VCK	Copied and modified from FDA-2253
// 
//	type: 0 == numeric; 1 == alphanumeric; anything else == no character restrictions
	var myString = field.rawValue;
	var validString = true;
	var numChars = myString.length;

	//check that minLength <= maxLength
	if (minLength > maxLength) {
		xfa.host.messageBox("Parameter error: minLength > maxLength");
	} else {
		if  ((myString < 0 ) || ((numChars > 0) && (numChars < minLength)) || (numChars > maxLength))  { //check that length falls within the specified range
			validString = false;
		} else {
			if (type == 0) { //numeric: ensure that all characters are digits
				for (i = 0; i < numChars; i++) {
					var oneChar = myString.charAt(i);
					if ((isNaN(oneChar)) || (oneChar == " ")) {
						validString = false;
					}
				}
			} else {
				if (type == 1) { //alphanumeric: regular expression checks for non-alphanumeric characters
					var myRegExp = /\W/;
					if (myRegExp.test(myString)) {
						validString = false;
					}
					for (i = 0; i < numChars; i++) { //veto underscores ("alphanumeric" usually includes them)
						var oneChar = myString.charAt(i);
						if (oneChar == "_") {
							validString = false;
						}
					}
				}
			}
		}
		if (validString == false)  { //Error messages vary depending on length requirements (absolute, maximum, specific range) and type
			if (type == 0) { //numeric
				if (minLength == maxLength) {
					xfa.host.messageBox("Please enter a " + maxLength + "-digit number.");
				} else if (minLength == 1) {
					xfa.host.messageBox("Please enter a number of up to " + maxLength + " digits.");
				} else {
					xfa.host.messageBox("Please enter a number of between " + minLength + " and " + maxLength +  " digits.");
				}
			} else {
				if (type == 1) { //alphanumeric
					if (minLength == maxLength) {
						xfa.host.messageBox("Please enter a " + maxLength + "-character alphanumeric string.");
					} else if (minLength == 1) {
						xfa.host.messageBox("Please enter an alphanumeric string of up to " + maxLength + " characters.");
					} else {
						xfa.host.messageBox("Please enter an alphanumeric string of between " + minLength + " and " + maxLength +  " characters.");
					}
				} else { //unrestricted
					if (minLength == maxLength) {
						xfa.host.messageBox("Please enter a " + maxLength + "-character string.");
					} else if (minLength == 1) {
						xfa.host.messageBox("Please enter a string of up to " + maxLength + " characters.");
					} else {
						xfa.host.messageBox("Please enter a string of between " + minLength + " and " + maxLength +  " characters.");
					}
				}
			}
			field.rawValue = ""; //clear field
			xfa.host.setFocus(field); //refocus on field
			false;
		} else { true; }
	}
}

function checkDate(field) {

	//  6-4-2013 DFG & 6-6-2013 VCK - Written by Valerie. Checks date for anything stupid user can do.
	var myString = field.rawValue;
	var validString = true;
	var numChars = myString.length;
	var month;
	var day;
	var year;
	var oneChar;

	if  ((myString < 0) || (numChars > 0 && numChars < 10) || (numChars > 10) )  { //MM/DD/YYYY must be 10 digits long
		validString = false;
	} else  {
		if (myString.charAt(2) == "/") { //MM/DD/YYYY
			for (i=0; i < 10 ; i++) {
				oneChar = myString.charAt(i);
				if (i == 2 || i == 5) {
					if (oneChar != "/") {
						validString = false;
					}
				} else if ((isNaN(oneChar)) || (oneChar == " ")) {
					validString = false;
				} else { //assign month, day, year values
					month = parseInt(myString.substring(0,2));
					day = parseInt(myString.substring(3,5));
					year = parseInt(myString.substring(6));
				}
			}
		} else { //YYYY-MM-DD ("invisible" date format when using the calendar selector)
			for (i=0; i < 10 ; i++) {
				oneChar = myString.charAt(i);
				if (i == 4 || i == 7) {
					if (oneChar != "-") {
						validString = false;
					}
				} else if ((isNaN(oneChar)) || (oneChar == " ")) {
					validString = false;
				} else { //assign year, month, day values
					year = parseInt(myString.substring(0,4));
					month = parseInt(myString.substring(5,7));
					day = parseInt(myString.substring(8));
				}
			}
		}
		if (month > 12) { //valid date check
			validString = false;
		} else {
			if (month == 2) {
				if ((year%400 == 0) || (year%4 == 0 && year%100 != 0)) { //leap year
					if (day > 29) {
						validString = false;
					}
				} else {
					if (day > 28) {
						validString = false;
					}
				}
			} else if (month == 4 || month == 6 || month == 9 || month == 11) {
				if (day > 30) {
					validString = false;
				}
			} else if (day > 31) {
				validString = false;
			}
		}
	}
	if (validString == false)  {
		xfa.host.messageBox("Please enter a valid date in 2-digit month / 2-digit day / 4-digit year (MM/DD/YYYY) format.");
		field.rawValue = ""; //clear field
		xfa.host.setFocus(field); //refocus on field
		false;
	} else { true; }
}

